			this.modalClose();
			this.selectionRestore();

			var current = this.getBlock() || this.getCurrent();

			if (current && current.tagName != 'BODY') $(current).after(html)
			else this.insertHtmlAdvanced(html, false);

			this.selectionRestore();

			var table = this.$editor.find('#table' + tableId);
